mapper.update:
update post
set views=post.views + 1
where postid = ${param1}

mapper.select:
select postid, boardcategory, posttitle, date_format(date, '%y-%m-%d') date
from post

    where user_userid = #{searchKey}


    where user_userid = #{searchKey}
    and posttitle like concat('%',#{searchWord},'%')

    order by postid desc
limit #{onePageRecord} offset #{offsetPoint};

select count(postid)
from post
where user_userid = #{searchKey}

SELECT comment.commentid as commentid, comment.post_postid as post_postid, post.posttitle as username, comment.commentcontent as commentcontent, comment.date as date from comment join post on comment.post_postid = post.postid where comment.user_userid = #{searchKey} and isdelete = 0
and commentcontent like concat('%', #{searchWord} ,'%')
order by date desc limit #{onePageRecord} offset #{offsetPoint};

select count(commentid)
from comment
where user_userid = #{searchKey}
and isdelete = 0;

select count(user_userid)
