select a.applyid as applyid, s.subjecttitle as subjecttitle, a.applyimg as applyimg, s.category as category from subjectapply a left join subject s on a.subject_subjectid = s.subjectid where a.user_userid = #{searchKey} and a.isteam = 1
and s.subjecttitle like concat('%', #{searchWord}, '%')
limit #{onePageRecord} offset #{offsetPoint}

SELECT subjectapply.applyimg as applyimg, subject.subjecttitle as subjecttitle FROM subjectapply JOIN subject ON subject.subjectid = subjectapply.subject_subjectid left join subjectcontributor sc on subjectapply.subject_subjectid = sc.subjectapply_applyid WHERE sc.user_userid = #{searchKey} AND isteam = 0
AND subject.subjecttitle LIKE CONCAT('%', #{searchWord}, '%')
limit #{onePageRecord} offset #{offsetPoint}

