mapper.select:
select count(userid)
from user
where userid = #{param1};

select count(username)
from user
where username = #{param1};

select count(useremail)
from user
where useremail = #{param1};

mapper.update:
update company
set authimg = #{param1}
where companyno = #{param2}

mapper.select:
select userpwd, usersalt, usertype
from user
where userid = #{param1};

select companyno
from company
where user_userid = #{param1};

select isaccept
from company
where user_userid = #{param1};

select userid
from user
where useremail = #{param1} and usertype = #{param2};

select user.userid
from user
         join company on user.userid = company.user_userid
where company.companyno = #{param2}
  and user.useremail = #{param1}
and user.usertype = #{param3}
